// GLSL
declare module "*.glsl" {
  const value: string;
  export = value;
}

// Image file
declare module "*.png" {
  const value: any;
  export = value;
}
declare module "*.jpg" {
  const value: any;
  export = value;
}
declare module "*.gif" {
  const value: any;
  export = value;
}

